import{_ as a,c as t,o as n,a2 as r}from"./chunks/framework.Bj-1CPTZ.js";const b=JSON.parse('{"title":"Frequently Asked Questions","description":"","frontmatter":{},"headers":[],"relativePath":"user-guide/faq.md","filePath":"user-guide/faq.md","lastUpdated":1767998922000}'),i={name:"user-guide/faq.md"};function s(o,e,l,d,u,h){return n(),t("div",null,[...e[0]||(e[0]=[r('<h1 id="frequently-asked-questions" tabindex="-1">Frequently Asked Questions <a class="header-anchor" href="#frequently-asked-questions" aria-label="Permalink to &quot;Frequently Asked Questions&quot;">​</a></h1><p>Common questions about Centabit.</p><h2 id="general" tabindex="-1">General <a class="header-anchor" href="#general" aria-label="Permalink to &quot;General&quot;">​</a></h2><h3 id="is-my-data-private" tabindex="-1">Is my data private? <a class="header-anchor" href="#is-my-data-private" aria-label="Permalink to &quot;Is my data private?&quot;">​</a></h3><p>Yes! All data is stored locally on your device. If cloud sync is added in the future, it would be optional and encrypted.</p><h3 id="can-i-use-centabit-on-multiple-devices" tabindex="-1">Can I use Centabit on multiple devices? <a class="header-anchor" href="#can-i-use-centabit-on-multiple-devices" aria-label="Permalink to &quot;Can I use Centabit on multiple devices?&quot;">​</a></h3><p>Currently, data is stored locally on a single device. Multi-device support via cloud sync is under consideration based on user feedback.</p><h3 id="does-centabit-connect-to-my-bank" tabindex="-1">Does Centabit connect to my bank? <a class="header-anchor" href="#does-centabit-connect-to-my-bank" aria-label="Permalink to &quot;Does Centabit connect to my bank?&quot;">​</a></h3><p>No. You manually enter transactions. This keeps your banking credentials private and secure.</p><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h2><h3 id="how-does-bar-work" tabindex="-1">How does BAR work? <a class="header-anchor" href="#how-does-bar-work" aria-label="Permalink to &quot;How does BAR work?&quot;">​</a></h3><p>See <a href="./understanding-bar.html">Understanding BAR</a> for a detailed explanation.</p><h3 id="can-i-edit-or-delete-transactions" tabindex="-1">Can I edit or delete transactions? <a class="header-anchor" href="#can-i-edit-or-delete-transactions" aria-label="Permalink to &quot;Can I edit or delete transactions?&quot;">​</a></h3><p>Yes, tap any transaction to edit or delete it.</p><h3 id="can-i-have-multiple-budgets" tabindex="-1">Can I have multiple budgets? <a class="header-anchor" href="#can-i-have-multiple-budgets" aria-label="Permalink to &quot;Can I have multiple budgets?&quot;">​</a></h3><p>Yes! Create as many budgets as you need for different time periods or purposes.</p><h3 id="what-s-the-difference-between-a-budget-and-a-category" tabindex="-1">What&#39;s the difference between a budget and a category? <a class="header-anchor" href="#what-s-the-difference-between-a-budget-and-a-category" aria-label="Permalink to &quot;What&#39;s the difference between a budget and a category?&quot;">​</a></h3><ul><li><strong>Budget</strong>: A spending plan for a time period</li><li><strong>Category</strong>: A type of spending (groceries, entertainment, etc.)</li><li><strong>Allocation</strong>: How much of a budget is assigned to each category</li></ul><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><h3 id="my-bar-seems-wrong" tabindex="-1">My BAR seems wrong <a class="header-anchor" href="#my-bar-seems-wrong" aria-label="Permalink to &quot;My BAR seems wrong&quot;">​</a></h3><ul><li>Check that all transactions are assigned to the correct budget</li><li>Verify budget start/end dates are correct</li><li>Make sure transaction dates are accurate</li></ul><h3 id="transactions-aren-t-showing-in-reports" tabindex="-1">Transactions aren&#39;t showing in reports <a class="header-anchor" href="#transactions-aren-t-showing-in-reports" aria-label="Permalink to &quot;Transactions aren&#39;t showing in reports&quot;">​</a></h3><ul><li>Check the date filter range</li><li>Verify transactions have categories assigned</li><li>Try refreshing the app</li></ul><h3 id="app-is-running-slow" tabindex="-1">App is running slow <a class="header-anchor" href="#app-is-running-slow" aria-label="Permalink to &quot;App is running slow&quot;">​</a></h3><ul><li>Close and restart the app</li><li>Check available storage space</li><li>Update to the latest version</li></ul><h2 id="getting-help" tabindex="-1">Getting Help <a class="header-anchor" href="#getting-help" aria-label="Permalink to &quot;Getting Help&quot;">​</a></h2><ul><li><a href="https://github.com/domain80/centabit_v0.5/issues" target="_blank" rel="noreferrer">GitHub Issues</a> - Report bugs</li><li><a href="https://github.com/domain80/centabit_v0.5/discussions" target="_blank" rel="noreferrer">GitHub Discussions</a> - Ask questions</li><li><a href="https://tally.so/r/eqQo4k" target="_blank" rel="noreferrer">Join Waitlist</a> - Get updates</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li><a href="./creating-budgets.html">Creating Budgets</a></li><li><a href="./tracking-transactions.html">Tracking Transactions</a></li><li><a href="./understanding-bar.html">Understanding BAR</a></li></ul>',29)])])}const p=a(i,[["render",s]]);export{b as __pageData,p as default};
