import{_ as t,c as e,o as d,a2 as r}from"./chunks/framework.Bj-1CPTZ.js";const c=JSON.parse('{"title":"Understanding BAR (Budget Adherence Ratio)","description":"","frontmatter":{},"headers":[],"relativePath":"user-guide/understanding-bar.md","filePath":"user-guide/understanding-bar.md","lastUpdated":1768003268000}'),n={name:"user-guide/understanding-bar.md"};function i(o,a,s,l,h,v){return d(),e("div",null,[...a[0]||(a[0]=[r('<h1 id="understanding-bar-budget-adherence-ratio" tabindex="-1" data-v-d5a77768>Understanding BAR (Budget Adherence Ratio) <a class="header-anchor" href="#understanding-bar-budget-adherence-ratio" aria-label="Permalink to &quot;Understanding BAR (Budget Adherence Ratio)&quot;" data-v-d5a77768>â€‹</a></h1><p data-v-d5a77768>Learn about the Budget Adherence Ratio and how to use it to manage your spending.</p><h2 id="what-is-bar" tabindex="-1" data-v-d5a77768>What is BAR? <a class="header-anchor" href="#what-is-bar" aria-label="Permalink to &quot;What is BAR?&quot;" data-v-d5a77768>â€‹</a></h2><p data-v-d5a77768>BAR (Budget Adherence Ratio) is a smart financial health metric that compares your actual spending to your expected spending. It helps you understand if you&#39;re on track to stay within budget.</p><p data-v-d5a77768><strong data-v-d5a77768>What makes it &quot;smart&quot;?</strong> Unlike simple calculations that assume you spend the same amount every day, Centabit&#39;s BAR uses a <strong data-v-d5a77768>front-loaded spending curve</strong> that reflects real spending behavior: people tend to spend more right after receiving income (payday effect).</p><h2 id="the-formula" tabindex="-1" data-v-d5a77768>The Formula <a class="header-anchor" href="#the-formula" aria-label="Permalink to &quot;The Formula&quot;" data-v-d5a77768>â€‹</a></h2><div class="language- vp-adaptive-theme line-numbers-mode" data-v-d5a77768><button title="Copy Code" class="copy" data-v-d5a77768></button><span class="lang" data-v-d5a77768></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0" data-v-d5a77768><code data-v-d5a77768><span class="line" data-v-d5a77768><span data-v-d5a77768>BAR = Actual Spent / Expected Spent</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true" data-v-d5a77768><span class="line-number" data-v-d5a77768>1</span><br data-v-d5a77768></div></div><p data-v-d5a77768>where <strong data-v-d5a77768>Expected Spent</strong> uses a front-loaded curve:</p><div class="language- vp-adaptive-theme line-numbers-mode" data-v-d5a77768><button title="Copy Code" class="copy" data-v-d5a77768></button><span class="lang" data-v-d5a77768></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0" data-v-d5a77768><code data-v-d5a77768><span class="line" data-v-d5a77768><span data-v-d5a77768>Expected = TotalBudget Ã— (1.2t - 0.2tÂ²)</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true" data-v-d5a77768><span class="line-number" data-v-d5a77768>1</span><br data-v-d5a77768></div></div><ul data-v-d5a77768><li data-v-d5a77768><code data-v-d5a77768>t</code> = time elapsed (0 to 1, where 0.5 is halfway through period)</li><li data-v-d5a77768><code data-v-d5a77768>1.2</code> = front-load factor (spending happens 20% faster early on)</li></ul><h2 id="interactive-calculator" tabindex="-1" data-v-d5a77768>Interactive Calculator <a class="header-anchor" href="#interactive-calculator" aria-label="Permalink to &quot;Interactive Calculator&quot;" data-v-d5a77768>â€‹</a></h2><p data-v-d5a77768>Try our interactive BAR calculator to see how your spending compares to expectations:</p><div style="text-align:center;margin:2rem 0;" data-v-d5a77768><a href="./bar-calculator" class="calculator-link" data-v-d5a77768><span class="calculator-icon" data-v-d5a77768>ğŸ§®</span><span class="calculator-text" data-v-d5a77768>Open BAR Calculator</span></a></div><p data-v-d5a77768><strong data-v-d5a77768>Calculator features:</strong></p><ul data-v-d5a77768><li data-v-d5a77768>Adjust budget amount, period, and spending</li><li data-v-d5a77768>See real-time BAR calculation</li><li data-v-d5a77768>Visualize spending curves (linear vs front-loaded)</li><li data-v-d5a77768>Customize front-load factor for different spending patterns</li><li data-v-d5a77768>Interactive chart showing expected vs actual spending</li></ul><h2 id="how-it-works" tabindex="-1" data-v-d5a77768>How It Works <a class="header-anchor" href="#how-it-works" aria-label="Permalink to &quot;How It Works&quot;" data-v-d5a77768>â€‹</a></h2><h3 id="linear-vs-smart-bar" tabindex="-1" data-v-d5a77768>Linear vs Smart BAR <a class="header-anchor" href="#linear-vs-smart-bar" aria-label="Permalink to &quot;Linear vs Smart BAR&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768><strong data-v-d5a77768>Old way (linear):</strong></p><ul data-v-d5a77768><li data-v-d5a77768>Assumes you spend the same amount every day</li><li data-v-d5a77768>At 50% of time â†’ expects 50% of budget spent</li></ul><p data-v-d5a77768><strong data-v-d5a77768>Smart way (front-loaded):</strong></p><ul data-v-d5a77768><li data-v-d5a77768>Recognizes you spend more after payday</li><li data-v-d5a77768>At 50% of time â†’ expects 55% of budget spent</li></ul><p data-v-d5a77768>This means the smart BAR is more forgiving early in the period!</p><h3 id="example-mid-budget-comparison" tabindex="-1" data-v-d5a77768>Example: Mid-Budget Comparison <a class="header-anchor" href="#example-mid-budget-comparison" aria-label="Permalink to &quot;Example: Mid-Budget Comparison&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768><strong data-v-d5a77768>Budget:</strong> $1,500 for 30 days <strong data-v-d5a77768>Day 15</strong> (50% through period) <strong data-v-d5a77768>Spent:</strong> $800</p><p data-v-d5a77768><strong data-v-d5a77768>Linear calculation:</strong></p><ul data-v-d5a77768><li data-v-d5a77768>Expected: $750 (50% of $1,500)</li><li data-v-d5a77768>BAR: 800/750 = 1.07 âš ï¸ (slightly over)</li></ul><p data-v-d5a77768><strong data-v-d5a77768>Smart calculation (Centabit):</strong></p><ul data-v-d5a77768><li data-v-d5a77768>Expected: $825 (55% of $1,500)</li><li data-v-d5a77768>BAR: 800/825 = 0.97 âœ“ (right on track!)</li></ul><p data-v-d5a77768>The smart calculation recognizes that $800 spent halfway through is actually good progress!</p><h2 id="interpreting-bar" tabindex="-1" data-v-d5a77768>Interpreting BAR <a class="header-anchor" href="#interpreting-bar" aria-label="Permalink to &quot;Interpreting BAR&quot;" data-v-d5a77768>â€‹</a></h2><h3 id="bar-0-85-ğŸ‰" tabindex="-1" data-v-d5a77768>BAR &lt; 0.85 ğŸ‰ <a class="header-anchor" href="#bar-0-85-ğŸ‰" aria-label="Permalink to &quot;BAR &lt; 0.85 ğŸ‰&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768><strong data-v-d5a77768>Well under budget!</strong> You&#39;re spending much slower than expected. Great job!</p><h3 id="bar-0-85-0-95-âœ“" tabindex="-1" data-v-d5a77768>BAR 0.85-0.95 âœ“ <a class="header-anchor" href="#bar-0-85-0-95-âœ“" aria-label="Permalink to &quot;BAR 0.85-0.95 âœ“&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768><strong data-v-d5a77768>Slightly under budget.</strong> You&#39;re doing well and have breathing room.</p><h3 id="bar-0-95-1-05-âœ“" tabindex="-1" data-v-d5a77768>BAR 0.95-1.05 âœ“ <a class="header-anchor" href="#bar-0-95-1-05-âœ“" aria-label="Permalink to &quot;BAR 0.95-1.05 âœ“&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768><strong data-v-d5a77768>Right on track!</strong> Your spending matches expectations perfectly.</p><h3 id="bar-1-05-1-15-âš ï¸" tabindex="-1" data-v-d5a77768>BAR 1.05-1.15 âš ï¸ <a class="header-anchor" href="#bar-1-05-1-15-âš ï¸" aria-label="Permalink to &quot;BAR 1.05-1.15 âš ï¸&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768><strong data-v-d5a77768>Slightly over budget.</strong> Time to review spending in this category.</p><h3 id="bar-1-15-ğŸš¨" tabindex="-1" data-v-d5a77768>BAR &gt; 1.15 ğŸš¨ <a class="header-anchor" href="#bar-1-15-ğŸš¨" aria-label="Permalink to &quot;BAR &gt; 1.15 ğŸš¨&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768><strong data-v-d5a77768>Significantly over budget!</strong> You&#39;re spending much faster than expected. Action needed!</p><h2 id="visual-indicators" tabindex="-1" data-v-d5a77768>Visual Indicators <a class="header-anchor" href="#visual-indicators" aria-label="Permalink to &quot;Visual Indicators&quot;" data-v-d5a77768>â€‹</a></h2><p data-v-d5a77768>The dashboard shows BAR with color-coded progress:</p><ul data-v-d5a77768><li data-v-d5a77768><strong data-v-d5a77768>Green</strong>: On track or under budget (BAR â‰¤ 1.05)</li><li data-v-d5a77768><strong data-v-d5a77768>Yellow/Orange</strong>: Warning zone (BAR 1.05-1.15)</li><li data-v-d5a77768><strong data-v-d5a77768>Red</strong>: Over budget (BAR &gt; 1.15)</li></ul><h2 id="real-world-examples" tabindex="-1" data-v-d5a77768>Real-World Examples <a class="header-anchor" href="#real-world-examples" aria-label="Permalink to &quot;Real-World Examples&quot;" data-v-d5a77768>â€‹</a></h2><h3 id="example-1-early-period-spending" tabindex="-1" data-v-d5a77768>Example 1: Early Period Spending <a class="header-anchor" href="#example-1-early-period-spending" aria-label="Permalink to &quot;Example 1: Early Period Spending&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768><strong data-v-d5a77768>Budget:</strong> $1,000 for 30 days <strong data-v-d5a77768>Day 10</strong> (33% through period) <strong data-v-d5a77768>Spent:</strong> $450</p><p data-v-d5a77768><strong data-v-d5a77768>Analysis:</strong></p><ul data-v-d5a77768><li data-v-d5a77768>Expected (smart): $433 (front-loaded curve)</li><li data-v-d5a77768>BAR: 450/433 = 1.04 âœ“ (right on track!)</li><li data-v-d5a77768>You spent a bit more early (payday), but that&#39;s expected behavior</li></ul><h3 id="example-2-mid-period-check" tabindex="-1" data-v-d5a77768>Example 2: Mid-Period Check <a class="header-anchor" href="#example-2-mid-period-check" aria-label="Permalink to &quot;Example 2: Mid-Period Check&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768><strong data-v-d5a77768>Budget:</strong> $2,000 for 30 days <strong data-v-d5a77768>Day 20</strong> (67% through period) <strong data-v-d5a77768>Spent:</strong> $1,400</p><p data-v-d5a77768><strong data-v-d5a77768>Analysis:</strong></p><ul data-v-d5a77768><li data-v-d5a77768>Expected (smart): $1,662 (front-loaded curve)</li><li data-v-d5a77768>BAR: 1400/1662 = 0.84 ğŸ‰ (well under budget!)</li><li data-v-d5a77768>You&#39;ve been careful with spending - great progress</li></ul><h3 id="example-3-overspending-alert" tabindex="-1" data-v-d5a77768>Example 3: Overspending Alert <a class="header-anchor" href="#example-3-overspending-alert" aria-label="Permalink to &quot;Example 3: Overspending Alert&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768><strong data-v-d5a77768>Budget:</strong> $800 for 30 days <strong data-v-d5a77768>Day 12</strong> (40% through period) <strong data-v-d5a77768>Spent:</strong> $550</p><p data-v-d5a77768><strong data-v-d5a77768>Analysis:</strong></p><ul data-v-d5a77768><li data-v-d5a77768>Expected (smart): $416 (front-loaded curve)</li><li data-v-d5a77768>BAR: 550/416 = 1.32 ğŸš¨ (significantly over!)</li><li data-v-d5a77768>You need to reduce spending immediately to stay on budget</li></ul><h2 id="using-bar-effectively" tabindex="-1" data-v-d5a77768>Using BAR Effectively <a class="header-anchor" href="#using-bar-effectively" aria-label="Permalink to &quot;Using BAR Effectively&quot;" data-v-d5a77768>â€‹</a></h2><h3 id="_1-check-regularly" tabindex="-1" data-v-d5a77768>1. Check Regularly <a class="header-anchor" href="#_1-check-regularly" aria-label="Permalink to &quot;1. Check Regularly&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768>Review your BAR at least twice per week to catch issues early.</p><h3 id="_2-understand-the-pattern" tabindex="-1" data-v-d5a77768>2. Understand the Pattern <a class="header-anchor" href="#_2-understand-the-pattern" aria-label="Permalink to &quot;2. Understand the Pattern&quot;" data-v-d5a77768>â€‹</a></h3><ul data-v-d5a77768><li data-v-d5a77768><strong data-v-d5a77768>Week 1-2:</strong> Higher spending is normal (payday effect)</li><li data-v-d5a77768><strong data-v-d5a77768>Week 3-4:</strong> Spending should naturally slow down</li></ul><h3 id="_3-take-action-on-high-bar" tabindex="-1" data-v-d5a77768>3. Take Action on High BAR <a class="header-anchor" href="#_3-take-action-on-high-bar" aria-label="Permalink to &quot;3. Take Action on High BAR&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768>When BAR &gt; 1.05:</p><ul data-v-d5a77768><li data-v-d5a77768>Review recent transactions in that category</li><li data-v-d5a77768>Identify unnecessary spending</li><li data-v-d5a77768>Set a daily/weekly spending limit</li><li data-v-d5a77768>Consider moving money between categories</li></ul><h3 id="_4-don-t-panic-at-temporary-spikes" tabindex="-1" data-v-d5a77768>4. Don&#39;t Panic at Temporary Spikes <a class="header-anchor" href="#_4-don-t-panic-at-temporary-spikes" aria-label="Permalink to &quot;4. Don&#39;t Panic at Temporary Spikes&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768>One large purchase can spike your BAR. Wait a few days to see the trend before overreacting.</p><h3 id="_5-track-patterns-over-time" tabindex="-1" data-v-d5a77768>5. Track Patterns Over Time <a class="header-anchor" href="#_5-track-patterns-over-time" aria-label="Permalink to &quot;5. Track Patterns Over Time&quot;" data-v-d5a77768>â€‹</a></h3><p data-v-d5a77768>After a few budget periods, you&#39;ll notice:</p><ul data-v-d5a77768><li data-v-d5a77768>Which categories consistently have high BAR</li><li data-v-d5a77768>When in the month you tend to overspend</li><li data-v-d5a77768>Your natural spending rhythm</li></ul><h2 id="tips-for-staying-on-track" tabindex="-1" data-v-d5a77768>Tips for Staying on Track <a class="header-anchor" href="#tips-for-staying-on-track" aria-label="Permalink to &quot;Tips for Staying on Track&quot;" data-v-d5a77768>â€‹</a></h2><p data-v-d5a77768>âœ… <strong data-v-d5a77768>Set up budgets aligned with pay periods</strong> Start your budget on payday for better alignment</p><p data-v-d5a77768>âœ… <strong data-v-d5a77768>Review BAR every 3-4 days</strong> Frequent check-ins help you catch issues early</p><p data-v-d5a77768>âœ… <strong data-v-d5a77768>Use the dashboard charts</strong> Visual comparison of planned vs actual helps identify problem areas</p><p data-v-d5a77768>âœ… <strong data-v-d5a77768>Adjust future budgets based on patterns</strong> If your BAR is consistently high in a category, you may need to budget more for it</p><p data-v-d5a77768>âŒ <strong data-v-d5a77768>Don&#39;t obsess over daily changes</strong> BAR fluctuates - focus on the weekly trend</p><p data-v-d5a77768>âŒ <strong data-v-d5a77768>Don&#39;t ignore BAR &gt; 1.15</strong> High BAR requires immediate action to avoid running out of budget</p><h2 id="advanced-how-the-curve-works" tabindex="-1" data-v-d5a77768>Advanced: How the Curve Works <a class="header-anchor" href="#advanced-how-the-curve-works" aria-label="Permalink to &quot;Advanced: How the Curve Works&quot;" data-v-d5a77768>â€‹</a></h2><p data-v-d5a77768>The front-loaded spending curve is based on a mathematical formula that makes spending expectations higher early in the period:</p><p data-v-d5a77768><strong data-v-d5a77768>At 25% of time:</strong> Expects 28.75% of budget (vs 25% linear) <strong data-v-d5a77768>At 50% of time:</strong> Expects 55% of budget (vs 50% linear) <strong data-v-d5a77768>At 75% of time:</strong> Expects 78.75% of budget (vs 75% linear) <strong data-v-d5a77768>At 100% of time:</strong> Expects 100% of budget (same as linear)</p><p data-v-d5a77768>This reflects real spending behavior where people spend more in the first half of a budget period.</p><h2 id="potential-enhancement-historical-learning" tabindex="-1" data-v-d5a77768>Potential Enhancement: Historical Learning <a class="header-anchor" href="#potential-enhancement-historical-learning" aria-label="Permalink to &quot;Potential Enhancement: Historical Learning&quot;" data-v-d5a77768>â€‹</a></h2><p data-v-d5a77768>One possible future enhancement is personalized BAR based on your spending patterns. This would:</p><ul data-v-d5a77768><li data-v-d5a77768>Analyze your historical spending curves</li><li data-v-d5a77768>Blend your patterns with the front-loaded curve</li><li data-v-d5a77768>Provide more personalized BAR calculations</li><li data-v-d5a77768>Give insights into your unique spending rhythm</li></ul><p data-v-d5a77768><strong data-v-d5a77768>Have thoughts on this?</strong> <a href="https://tally.so/r/eqQo4k" target="_blank" rel="noreferrer" data-v-d5a77768>Join the free waitlist</a> and share your feedback!</p><h2 id="next-steps" tabindex="-1" data-v-d5a77768>Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;" data-v-d5a77768>â€‹</a></h2><ul data-v-d5a77768><li data-v-d5a77768><a href="./creating-budgets.html" data-v-d5a77768>Creating Budgets</a></li><li data-v-d5a77768><a href="./tracking-transactions.html" data-v-d5a77768>Tracking Transactions</a></li><li data-v-d5a77768><a href="./categories.html" data-v-d5a77768>Managing Categories</a></li></ul>',86)])])}const u=t(n,[["render",i],["__scopeId","data-v-d5a77768"]]);export{c as __pageData,u as default};
